package com.links.ressys.core;

// TODO: Auto-generated Javadoc
/**
 * The Class Customer.
 */
public abstract class Customer{	
	
	/**
	 * Gets the tax code.
	 *
	 * @return the tax code
	 */
	public abstract String getTaxCode();

	/**
	 * Sets the tax code.
	 *
	 * @param taxCode the new tax code
	 */
	public abstract void setTaxCode(String taxCode);

	/**
	 * Gets the name.
	 *
	 * @return the name
	 */
	public abstract String getName();

	/**
	 * Sets the name.
	 *
	 * @param name the new name
	 */
	public abstract void setName(String name);

	/**
	 * Gets the surname.
	 *
	 * @return the surname
	 */
	public abstract String getSurname();

	/**
	 * Sets the surname.
	 *
	 * @param surname the new surname
	 */
	public abstract void setSurname(String surname);

	/**
	 * Gets the cell phone number.
	 *
	 * @return the cell phone number
	 */
	public abstract String getCellPhoneNumber();

	/**
	 * Sets the cell phone number.
	 *
	 * @param cellPhoneNumber the new cell phone number
	 */
	public abstract void setCellPhoneNumber(String cellPhoneNumber);

	/**
	 * Gets the mail address.
	 *
	 * @return the mail address
	 */
	public abstract String getMailAddress();

	/**
	 * Sets the mail address.
	 *
	 * @param mailAddress the new mail address
	 */
	public abstract void setMailAddress(String mailAddress);

	/**
	 * Gets the card number.
	 *
	 * @return the card number
	 */
	public abstract String getCardNumber();

	/**
	 * Sets the card number.
	 *
	 * @param cardNumber the new card number
	 */
	public abstract void setCardNumber(String cardNumber);
}
